import React, { useState, useEffect } from 'react';
import './App.css';
import { Card, DeckState } from './types';
import ImportCards from './components/ImportCards';

function App() {
  const [deckState, setDeckState] = useState<DeckState>({
    cards: [],
    queue: [],
    queueSize: 5, // default queue size
  });
  const [currentCard, setCurrentCard] = useState<Card | null>(null);
  const [showAnswer, setShowAnswer] = useState(false);

  const handleShowAnswer = () => {
    setShowAnswer(true);
  };

  const handleResponse = (remembered: boolean) => {
    if (!currentCard) return;

    if (remembered) {
      // Remove from queue and add new card if available
      const newQueue = deckState.queue.filter(card => card.id !== currentCard.id);
      const availableCards = deckState.cards.filter(
        card => !newQueue.some(qCard => qCard.id === card.id)
      );

      if (availableCards.length > 0) {
        const randomIndex = Math.floor(Math.random() * availableCards.length);
        newQueue.push(availableCards[randomIndex]);
      }

      setDeckState(prev => ({
        ...prev,
        queue: newQueue,
      }));
    } else {
      // Move current card to the end of the queue
      const newQueue = [
        ...deckState.queue.filter(card => card.id !== currentCard.id),
        currentCard,
      ];
      setDeckState(prev => ({
        ...prev,
        queue: newQueue,
      }));
    }

    setShowAnswer(false);
    // Next card will be set by useEffect
  };

  useEffect(() => {
    if (deckState.queue.length > 0) {
      setCurrentCard(deckState.queue[0]);
    } else {
      setCurrentCard(null);
    }
  }, [deckState.queue]);

  const handleImportCards = (newCards: Card[]) => {
    setDeckState(prev => ({
      ...prev,
      cards: newCards,
      queue: newCards.slice(0, prev.queueSize)
    }));
  };

  // Update keyboard event handler
  useEffect(() => {
    const handleKeyPress = (event: KeyboardEvent) => {
      if (!currentCard) return;

      switch (event.key) {
        case ' ': // Space key
          event.preventDefault(); // Prevent page scroll
          if (!showAnswer) {
            handleShowAnswer();
          } else {
            handleResponse(true);
          }
          break;
        case 'x':
        case 'X':
          if (showAnswer) {
            handleResponse(false);
          }
          break;
      }
    };

    window.addEventListener('keydown', handleKeyPress);
    return () => window.removeEventListener('keydown', handleKeyPress);
  }, [currentCard, showAnswer]); // Dependencies

  return (
    <div className="App">
      <div className="cramming-container">
        {deckState.cards.length === 0 ? (
          <ImportCards onImport={handleImportCards} />
        ) : (
          currentCard ? (
            <div className="card-display">
              <div className="prompt">
                {currentCard.prompt}
                {currentCard.hasImage && (
                  <img src={currentCard.imageUrl} alt="Prompt illustration" />
                )}
              </div>

              {!showAnswer ? (
                <button onClick={handleShowAnswer}>
                  Show Answer <span className="key-hint">(Space)</span>
                </button>
              ) : (
                <>
                  <div className="answer">
                    {currentCard.answer}
                  </div>
                  <div className="response-buttons">
                    <button onClick={() => handleResponse(true)}>
                      Remembered <span className="key-hint">(Space)</span>
                    </button>
                    <button onClick={() => handleResponse(false)}>
                      Forgot <span className="key-hint">(X)</span>
                    </button>
                  </div>
                </>
              )}
            </div>
          ) : (
            <div className="no-cards">
              <p>No cards available. Please import an Anki deck to begin.</p>
            </div>
          )
        )}
      </div>
    </div>
  );
}

export default App;
