import React, { useState, useEffect } from 'react';
import './App.css';
import { Card, DeckState } from './types';
import ImportCards from './components/ImportCards';

function App() {
  const [deckState, setDeckState] = useState<DeckState>(() => {
    const savedState = localStorage.getItem('deckState');
    return savedState ? JSON.parse(savedState) : {
      cards: [],
      queue: [],
      queueSize: 5
    };
  });
  const [currentCard, setCurrentCard] = useState<Card | null>(null);
  const [showAnswer, setShowAnswer] = useState(false);

  useEffect(() => {
    localStorage.setItem('deckState', JSON.stringify(deckState));
  }, [deckState]);

  const handleReset = () => {
    const newState = {
      cards: deckState.cards,
      queue: deckState.cards.slice(0, deckState.queueSize),
      queueSize: deckState.queueSize
    };
    setDeckState(newState);
    setShowAnswer(false);
  };

  const handleQueueSizeChange = (newSize: number) => {
    setDeckState(prev => ({
      ...prev,
      queueSize: newSize,
      queue: prev.queue.slice(0, newSize)
    }));
  };

  const handleShowAnswer = () => {
    setShowAnswer(true);
  };

  const handleResponse = (remembered: boolean) => {
    if (!currentCard) return;

    if (remembered) {
      // Remove from queue and add new card if available
      const newQueue = deckState.queue.filter(card => card.id !== currentCard.id);
      const availableCards = deckState.cards.filter(
        card => !newQueue.some(qCard => qCard.id === card.id)
      );

      if (availableCards.length > 0 && newQueue.length < deckState.queueSize) {
        const randomIndex = Math.floor(Math.random() * availableCards.length);
        newQueue.push(availableCards[randomIndex]);
      }

      setDeckState(prev => ({
        ...prev,
        queue: newQueue,
      }));
    } else {
      // Move current card to the end of the queue, maintaining queue size
      const remainingCards = deckState.queue.filter(card => card.id !== currentCard.id);
      const newQueue = [...remainingCards];

      // Only add the current card back if we haven't exceeded queue size
      if (newQueue.length < deckState.queueSize) {
        newQueue.push(currentCard);
      }

      setDeckState(prev => ({
        ...prev,
        queue: newQueue,
      }));
    }

    setShowAnswer(false);
  };

  const handleImportCards = (newCards: Card[]) => {
    setDeckState(prev => ({
      ...prev,
      cards: newCards,
      queue: newCards.slice(0, prev.queueSize)
    }));
  };

  useEffect(() => {
    if (deckState.queue.length > 0) {
      setCurrentCard(deckState.queue[0]);
    } else {
      setCurrentCard(null);
    }
  }, [deckState.queue]);

  useEffect(() => {
    const handleKeyPress = (event: KeyboardEvent) => {
      if (!currentCard) return;

      switch (event.key) {
        case ' ': // Space key
          event.preventDefault(); // Prevent page scroll
          if (!showAnswer) {
            handleShowAnswer();
          } else {
            handleResponse(true);
          }
          break;
        case 'x':
        case 'X':
          if (showAnswer) {
            handleResponse(false);
          }
          break;
      }
    };

    window.addEventListener('keydown', handleKeyPress);
    return () => window.removeEventListener('keydown', handleKeyPress);
  }, [currentCard, showAnswer]);

  const handleLoadNewDeck = () => {
    setDeckState({
      cards: [],
      queue: [],
      queueSize: deckState.queueSize
    });
    setCurrentCard(null);
    setShowAnswer(false);
  };

  return (
    <div className="App">
      <div className="cramming-container">
        {currentCard ? (
          <>
            <div className="controls">
              <div className="control-buttons">
                <button onClick={handleReset}>Reset Queue</button>
                <button onClick={handleLoadNewDeck}>Load New Deck</button>
              </div>
              <div className="queue-size-control">
                <label>
                  Queue Size:
                  <input
                    type="number"
                    min="1"
                    max={deckState.cards.length}
                    value={deckState.queueSize}
                    onChange={(e) => handleQueueSizeChange(Math.max(1, parseInt(e.target.value) || 1))}
                  />
                </label>
              </div>
            </div>
            <div className="card-display">
              <div className="prompt">
                <div className="content">{currentCard.prompt}</div>
                {currentCard.promptImage && (
                  <img 
                    src={currentCard.promptImage} 
                    alt="Prompt illustration" 
                    className="card-image"
                  />
                )}
              </div>

              {!showAnswer ? (
                <button onClick={handleShowAnswer}>
                  Show Answer <span className="key-hint">(Space)</span>
                </button>
              ) : (
                <>
                  <div className="answer">
                    <div className="content">{currentCard.answer}</div>
                    {currentCard.answerImage && (
                      <img 
                        src={currentCard.answerImage} 
                        alt="Answer illustration" 
                        className="card-image"
                      />
                    )}
                  </div>
                  <div className="response-buttons">
                    <button onClick={() => handleResponse(true)}>
                      Remembered <span className="key-hint">(Space)</span>
                    </button>
                    <button onClick={() => handleResponse(false)}>
                      Forgot <span className="key-hint">(X)</span>
                    </button>
                  </div>
                </>
              )}
            </div>
          </>
        ) : (
          <ImportCards onImport={handleImportCards} />
        )}
      </div>
    </div>
  );
}

export default App;
