import React, { useState, useEffect } from 'react';
import './App.css';
import { Card, StudyCard, DeckState } from './types';
import ImportCards from './components/ImportCards';

function App() {
  const [deckState, setDeckState] = useState<DeckState>({
    cards: [],
    queue: [],
    queueSize: 5,
    isReverseModeEnabled: false
  });
  const [currentCard, setCurrentCard] = useState<StudyCard | null>(null);
  const [showAnswer, setShowAnswer] = useState(false);

  const createStudyCard = (card: Card, isReversed: boolean): StudyCard => ({
    ...card,
    isReversed
  });

  const getDisplayContent = (card: StudyCard) => {
    if (card.isReversed) {
      return {
        prompt: card.answer,
        promptImage: card.answerImage,
        answer: card.prompt,
        answerImage: card.promptImage
      };
    }
    return card;
  };

  const fillQueue = (cards: Card[], currentQueue: StudyCard[], targetSize: number) => {
    const newQueue = [...currentQueue];
    while (newQueue.length < targetSize && cards.length > 0) {
      const availableCards = cards.filter(card => 
        !newQueue.some(qCard => qCard.id === card.id)
      );

      if (availableCards.length === 0) break;

      const randomCard = availableCards[Math.floor(Math.random() * availableCards.length)];
      const isReversed = deckState.isReverseModeEnabled && Math.random() < 0.5;
      newQueue.push(createStudyCard(randomCard, isReversed));
    }
    return newQueue;
  };

  const toggleReverseMode = () => {
    setDeckState(prev => ({
      ...prev,
      isReverseModeEnabled: !prev.isReverseModeEnabled,
      queue: [] // Reset queue when changing mode
    }));
  };

  const handleImportCards = (newCards: Card[]) => {
    setDeckState(prev => ({
      ...prev,
      cards: newCards,
      queue: fillQueue(newCards, [], prev.queueSize)
    }));
  };

  const handleShowAnswer = () => {
    setShowAnswer(true);
  };

  const handleResponse = (remembered: boolean) => {
    if (!currentCard) return;

    if (remembered) {
      // Remove from queue and add new card if available
      const newQueue = deckState.queue.filter(card => card.id !== currentCard.id);
      const availableCards = deckState.cards.filter(
        card => !newQueue.some(qCard => qCard.id === card.id)
      );

      if (availableCards.length > 0) {
        const randomIndex = Math.floor(Math.random() * availableCards.length);
        newQueue.push(availableCards[randomIndex]);
      }

      setDeckState(prev => ({
        ...prev,
        queue: newQueue,
      }));
    } else {
      // Move current card to the end of the queue
      const newQueue = [
        ...deckState.queue.filter(card => card.id !== currentCard.id),
        currentCard,
      ];
      setDeckState(prev => ({
        ...prev,
        queue: newQueue,
      }));
    }

    setShowAnswer(false);
    // Next card will be set by useEffect
  };

  useEffect(() => {
    if (deckState.queue.length > 0) {
      setCurrentCard(deckState.queue[0]);
    } else if (deckState.cards.length > 0) {
      // Refill queue if empty but cards exist
      setDeckState(prev => ({
        ...prev,
        queue: fillQueue(prev.cards, [], prev.queueSize)
      }));
    } else {
      setCurrentCard(null);
    }
  }, [deckState.queue]);

  // Update keyboard event handler
  useEffect(() => {
    const handleKeyPress = (event: KeyboardEvent) => {
      if (!currentCard) return;

      switch (event.key) {
        case ' ': // Space key
          event.preventDefault(); // Prevent page scroll
          if (!showAnswer) {
            handleShowAnswer();
          } else {
            handleResponse(true);
          }
          break;
        case 'x':
        case 'X':
          if (showAnswer) {
            handleResponse(false);
          }
          break;
      }
    };

    window.addEventListener('keydown', handleKeyPress);
    return () => window.removeEventListener('keydown', handleKeyPress);
  }, [currentCard, showAnswer]); // Dependencies

  return (
    <div className="App">
      <div className="cramming-container">
        {deckState.cards.length > 0 && (
          <div className="controls">
            <label className="reverse-mode-toggle">
              <input
                type="checkbox"
                checked={deckState.isReverseModeEnabled}
                onChange={toggleReverseMode}
              />
              Practice both sides
            </label>
          </div>
        )}

        {currentCard ? (
          <div className="card-display">
            <div className="prompt">
              {getDisplayContent(currentCard).prompt}
              {getDisplayContent(currentCard).promptImage && (
                <img 
                  src={getDisplayContent(currentCard).promptImage} 
                  alt="Prompt illustration" 
                  className="card-image"
                />
              )}
            </div>

            {!showAnswer ? (
              <button onClick={handleShowAnswer}>
                Show Answer <span className="key-hint">(Space)</span>
              </button>
            ) : (
              <>
                <div className="answer">
                  <div className="content">{currentCard.answer}</div>
                  {currentCard.answerImage && (
                    <img 
                      src={currentCard.answerImage} 
                      alt="Answer illustration" 
                      className="card-image"
                    />
                  )}
                </div>
                <div className="response-buttons">
                  <button onClick={() => handleResponse(true)}>
                    Remembered <span className="key-hint">(Space)</span>
                  </button>
                  <button onClick={() => handleResponse(false)}>
                    Forgot <span className="key-hint">(X)</span>
                  </button>
                </div>
              </>
            )}
          </div>
        ) : (
          <div className="no-cards">
            <p>No cards available. Please import an Anki deck to begin.</p>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
